sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//gs.info("debug: " + current);
	// push glide record
	////var responseObject = {}; //hashmap object variable to store final response
	//var getData = new GlideRecordUtil().getGR("sc_req_item", "gr.sys_id"); //pass the table name and Record sys_id to get the GlideRecord instance
	////var ignoreFields = {"sys_created_on": true, "sys_updated_by": true}; //Pass the fields that you want to exclude in the final response
	//new GlideRecordUtil().populateFromGR(responseObject, getData, ignoreFields); //The main function to retrieve the data
	////new GlideRecordUtil().populateFromGR(responseObject, current, ignoreFields); //The main function to retrieve the data
	//gs.print(JSON.stringify(responseObject, null, 4)); //Decode the object
	////gs.info("debug: " + JSON.stringify(responseObject, null, 4));

	// Add your code here
	var rightnow = new GlideDateTime();
	var whenNeeded = new GlideDateTime(current.u_when_needed);

	//enable x_58872_needit.logging.verbosity value=debug
	gs.debug(current.number + '.1: rightnow: ' + rightnow.getDisplayValue() + '\nwhenNeeded: ' + whenNeeded.getDisplayValue());
	//enable x_58872_needit.logging.verbosity value=info
	gs.info(current.number + '.1: rightnow: ' + rightnow.getDisplayValue() + '\nwhenNeeded: ' + whenNeeded.getDisplayValue());
	//enable x_58872_needit.logging.verbosity value=warn
	gs.warn(current.number + '.1: rightnow: ' + rightnow.getDisplayValue() + '\nwhenNeeded: ' + whenNeeded.getDisplayValue());
	//enable x_58872_needit.logging.verbosity value=error or no value or no property
	gs.error(current.number + '.1: rightnow: ' + rightnow.getDisplayValue() + '\nwhenNeeded: ' + whenNeeded.getDisplayValue());

	//gs.addInfoMessage("WhenNeeded=" + whenNeeded.getDisplayValueInternal() );

	if (whenNeeded.before(rightnow)) {
		gs.debug(current.number + '.2a: When needed date cannot be in the past.');
		gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	} else {
		gs.debug(current.number + '.2b: When needed date OK');
	}

})(current, previous);
